package com.littlehow.apm.feign;


import com.littlehow.apm.base.ApmClassSourceManager;

import java.util.Base64;

/**
 * 对LoadBalancerFeignClient类进行修改
 * 又不想对其重新打包，所以就通过类加载器进行优先加载，防止官方jar包的LoadBalancerFeignClient被加载
 * @see org.springframework.cloud.netflix.feign.ribbon.LoadBalancerFeignClient
 *
 * @author littlehow
 */
class LoadBalancerFeignClientSource {
    static final String className = "org.springframework.cloud.netflix.feign.ribbon.LoadBalancerFeignClient";
    static {
        byte[] source = Base64.getDecoder().decode("");
        ApmClassSourceManager.register(className, source);
    }
}
